import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Button } from "antd";
import Divider from "../../components/Divider";
import moment from "moment";

function ProductInfo() {
  const [selectedImageIndex, setSelectedImageIndex] = React.useState(0);
  const navigate = useNavigate();
  const { id } = useParams();

  // Sample images for different categories
  const categoryImages = {
    electronics: [
      "https://images.unsplash.com/photo-1593642632823-8f785ba67e45?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1579586337278-3befd40fd17a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    furniture: [
      "https://images.unsplash.com/photo-1580480055273-228ff5388ef8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1555041469-a586c61ea9bc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1567016432779-094069958ea5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    fashion: [
      "https://images.unsplash.com/photo-1523275335684-37898b6baf30?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1526170375885-4d8ecf77b99f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    sports: [
      "https://images.unsplash.com/photo-1517649763962-0c623066013b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517963879433-6ad2b056d712?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517649763962-0c623066013b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    books: [
      "https://images.unsplash.com/photo-1544947950-fa07a98d237f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1541963463532-d68292c34b19?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1543002588-bfa74002ed7e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ]
  };

  // Mock product data
  const product = {
    _id: id,
    name: "Sample Product",
    description: "This is a detailed description of the sample product.",
    price: 999,
    category: "electronics",
    billAvailable: true,
    boxAvailable: true,
    accessoriesAvailable: true,
    warrantyAvailable: true,
    age: 2,
    images: categoryImages.electronics, // Default to electronics images
    bids: []
  };

  // Update images based on category
  React.useEffect(() => {
    if (product.category && categoryImages[product.category]) {
      product.images = categoryImages[product.category];
    }
  }, [product.category]);

  return (
    <div className="flex gap-5">
      <div className="w-1/2 flex flex-col gap-5">
        <div className="w-full h-96">
          <img
            src={product.images[selectedImageIndex]}
            className="w-full h-full object-cover rounded-md"
            alt=""
          />
        </div>
        <div className="flex gap-5">
          {product.images.map((image, index) => {
            return (
              <img
                src={image}
                className={`w-24 h-24 object-cover rounded-md cursor-pointer ${
                  selectedImageIndex === index ? "border-2 border-primary" : ""
                }`}
                alt=""
                onClick={() => setSelectedImageIndex(index)}
              />
            );
          })}
        </div>
      </div>

      <div className="w-1/2 flex flex-col gap-5">
        <div>
          <h1 className="text-2xl font-semibold text-orange-900">
            {product.name}
          </h1>
          <span>{product.description}</span>
        </div>

        <Divider />
        <div className="flex flex-col">
          <h1 className="text-2xl font-semibold text-orange-900">
            Product Details
          </h1>
          <div className="flex justify-between mt-2">
            <span>Price</span>
            <span>₹ {product.price}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Category</span>
            <span className="uppercase">{product.category}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Bill Available</span>
            <span>{product.billAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Box Available</span>
            <span>{product.boxAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Accessories Available</span>
            <span>{product.accessoriesAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Warranty Available</span>
            <span>{product.warrantyAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Purchased Year</span>
            <span>
              {moment().subtract(product.age, "years").format("YYYY")} ({product.age} years ago)
            </span>
          </div>
        </div>

        <Divider />
        <div className="flex flex-col gap-5">
          <h1 className="text-2xl font-semibold text-orange-900">Bids</h1>
          <div className="flex flex-col gap-5">
            {product.bids.length === 0 ? (
              <span>No bids yet</span>
            ) : (
              product.bids.map((bid) => {
                return (
                  <div className="flex justify-between items-center border border-gray-300 p-5 rounded">
                    <div>
                      <span className="font-semibold">{bid.user.name}</span>
                      <br />
                      <span className="text-gray-500">
                        {moment(bid.createdAt).format("MMM DD, YYYY hh:mm a")}
                      </span>
                    </div>
                    <span className="text-xl font-semibold text-green-700">
                      ₹ {bid.amount}
                    </span>
                  </div>
                );
              })
            )}
          </div>
          <Button type="primary" block>
            Place a Bid
          </Button>
        </div>
      </div>
    </div>
  );
}

export default ProductInfo;
