import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Button, Input, Modal, message } from "antd";
import Divider from "../../components/Divider";
import moment from "moment";

function ProductInfo() {
  const [selectedImageIndex, setSelectedImageIndex] = React.useState(0);
  const [showBidModal, setShowBidModal] = React.useState(false);
  const [bidAmount, setBidAmount] = React.useState("");
  const [bidMessage, setBidMessage] = React.useState("");
  const [bids, setBids] = React.useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  // Sample images for different categories - more specific to each product type
  const categoryImages = {
    electronics: [
      "https://images.unsplash.com/photo-1593642632823-8f785ba67e45?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1496181133206-80ce9b88a853?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517336714731-489689fd1ca8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    furniture: [
      "https://images.unsplash.com/photo-1580480055273-228ff5388ef8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1519947486511-46149fa0a254?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1592078615290-033ee584e267?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    fashion: [
      "https://images.unsplash.com/photo-1523275335684-37898b6baf30?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1524805444758-089113d48a6d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1549972574-8e3e1ed6a347?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    sports: [
      "https://images.unsplash.com/photo-1517649763962-0c623066013b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517963879433-6ad2b056d712?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517963879433-6ad2b056d712?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    books: [
      "https://images.unsplash.com/photo-1544947950-fa07a98d237f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1541963463532-d68292c34b19?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1543002588-bfa74002ed7e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ]
  };

  // Mock product data
  const product = {
    _id: id,
    name: "Sample Product",
    description: "This is a detailed description of the sample product.",
    price: 999,
    category: "electronics",
    billAvailable: true,
    boxAvailable: true,
    accessoriesAvailable: true,
    warrantyAvailable: true,
    age: 2,
    images: categoryImages.electronics, // Default to electronics images
    bids: []
  };

  // Update images based on category
  React.useEffect(() => {
    if (product.category && categoryImages[product.category]) {
      product.images = categoryImages[product.category];
    }
  }, [product.category]);

  // Handle placing a bid
  const handlePlaceBid = () => {
    if (!bidAmount) {
      message.error("Please enter a bid amount");
      return;
    }

    if (parseInt(bidAmount) <= product.price) {
      message.error("Bid amount must be higher than the current price");
      return;
    }

    const newBid = {
      id: Date.now(),
      amount: parseInt(bidAmount),
      message: bidMessage,
      createdAt: new Date(),
      user: {
        name: "John Doe", // Mock user
        email: "john@example.com"
      }
    };

    setBids([...bids, newBid]);
    setShowBidModal(false);
    setBidAmount("");
    setBidMessage("");
    message.success("Bid placed successfully!");
  };

  return (
    <div className="flex gap-5">
      <div className="w-1/2 flex flex-col gap-5">
        <div className="w-full h-96">
          <img
            src={product.images[selectedImageIndex]}
            className="w-full h-full object-cover rounded-md"
            alt=""
          />
        </div>
        <div className="flex gap-5">
          {product.images.map((image, index) => {
            return (
              <img
                src={image}
                className={`w-24 h-24 object-cover rounded-md cursor-pointer ${
                  selectedImageIndex === index ? "border-2 border-primary" : ""
                }`}
                alt=""
                onClick={() => setSelectedImageIndex(index)}
              />
            );
          })}
        </div>
      </div>

      <div className="w-1/2 flex flex-col gap-5">
        <div>
          <h1 className="text-2xl font-semibold text-orange-900">
            {product.name}
          </h1>
          <span>{product.description}</span>
        </div>

        <Divider />
        <div className="flex flex-col">
          <h1 className="text-2xl font-semibold text-orange-900">
            Product Details
          </h1>
          <div className="flex justify-between mt-2">
            <span>Price</span>
            <span>₹ {product.price}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Category</span>
            <span className="uppercase">{product.category}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Bill Available</span>
            <span>{product.billAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Box Available</span>
            <span>{product.boxAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Accessories Available</span>
            <span>{product.accessoriesAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Warranty Available</span>
            <span>{product.warrantyAvailable ? "Yes" : "No"}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Purchased Year</span>
            <span>
              {moment().subtract(product.age, "years").format("YYYY")} ({product.age} years ago)
            </span>
          </div>
        </div>

        <Divider />
        <div className="flex flex-col gap-5">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-semibold text-orange-900">Bids</h1>
            <Button type="primary" onClick={() => setShowBidModal(true)}>
              Place a Bid
            </Button>
          </div>
          <div className="flex flex-col gap-5">
            {bids.length === 0 ? (
              <span>No bids yet</span>
            ) : (
              bids.map((bid) => {
                return (
                  <div key={bid.id} className="flex justify-between items-center border border-gray-300 p-5 rounded">
                    <div>
                      <span className="font-semibold">{bid.user.name}</span>
                      <br />
                      <span className="text-gray-500">
                        {moment(bid.createdAt).format("MMM DD, YYYY hh:mm a")}
                      </span>
                      {bid.message && (
                        <div className="mt-2 text-gray-600">
                          {bid.message}
                        </div>
                      )}
                    </div>
                    <span className="text-xl font-semibold text-green-700">
                      ₹ {bid.amount}
                    </span>
                  </div>
                );
              })
            )}
          </div>
        </div>
      </div>

      {/* Bid Modal */}
      <Modal
        title="Place a Bid"
        open={showBidModal}
        onCancel={() => setShowBidModal(false)}
        footer={[
          <Button key="cancel" onClick={() => setShowBidModal(false)}>
            Cancel
          </Button>,
          <Button key="submit" type="primary" onClick={handlePlaceBid}>
            Place Bid
          </Button>
        ]}
      >
        <div className="flex flex-col gap-5">
          <div>
            <label className="block text-gray-700 mb-2">Bid Amount (₹)</label>
            <Input
              type="number"
              value={bidAmount}
              onChange={(e) => setBidAmount(e.target.value)}
              placeholder="Enter your bid amount"
              min={product.price + 1}
            />
            <span className="text-sm text-gray-500 mt-1">
              Minimum bid: ₹{product.price + 1}
            </span>
          </div>
          <div>
            <label className="block text-gray-700 mb-2">Message (Optional)</label>
            <Input.TextArea
              value={bidMessage}
              onChange={(e) => setBidMessage(e.target.value)}
              placeholder="Add a message with your bid"
              rows={4}
            />
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default ProductInfo;
