import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Button, Input, Modal, message } from "antd";
import Divider from "../../components/Divider";
import moment from "moment";

function ProductInfo() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedImageIndex, setSelectedImageIndex] = React.useState(0);
  const [showBidModal, setShowBidModal] = React.useState(false);
  const [bidAmount, setBidAmount] = React.useState("");
  const [bidMessage, setBidMessage] = React.useState("");
  const [bids, setBids] = React.useState([]);
  const [showPaymentModal, setShowPaymentModal] = React.useState(false);
  const [paymentAmount, setPaymentAmount] = React.useState("");

  // Check if this is a PG view
  const isPGView = window.location.pathname.includes('/pg/');

  // Mock PG data
  const mockPG = {
    _id: id,
    name: "Sunrise PG for Girls",
    price: 8000,
    location: "Koramangala, Bangalore",
    distance: "1.2 km",
    rating: 4.8,
    amenities: ["WiFi", "Laundry", "Food", "Security"],
    images: [
      "https://images.unsplash.com/photo-1522708323590-d24dbb6b0267?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1560449017-3e7fa6980f82?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    description: "Modern PG with all amenities near tech parks"
  };

  // Mock product data
  const mockProduct = {
    _id: id,
    name: "Sample Product",
    description: "This is a detailed description of the sample product.",
    price: 999,
    category: "electronics",
    billAvailable: true,
    boxAvailable: true,
    accessoriesAvailable: true,
    warrantyAvailable: true,
    age: 2,
    images: [
      "https://images.unsplash.com/photo-1496181133206-80ce9b88a853?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517336714731-489689fd1ca8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80",
      "https://images.unsplash.com/photo-1517336714731-489689fd1ca8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    ],
    bids: []
  };

  const item = isPGView ? mockPG : mockProduct;

  // Handle placing a bid
  const handlePlaceBid = () => {
    if (!bidAmount) {
      message.error("Please enter a bid amount");
      return;
    }

    if (parseInt(bidAmount) <= item.price) {
      message.error("Bid amount must be higher than the current price");
      return;
    }

    const newBid = {
      id: Date.now(),
      amount: parseInt(bidAmount),
      message: bidMessage,
      createdAt: new Date(),
      user: {
        name: "John Doe", // Mock user
        email: "john@example.com"
      }
    };

    setBids([...bids, newBid]);
    setShowBidModal(false);
    setBidAmount("");
    setBidMessage("");
    message.success("Bid placed successfully!");
  };

  // Handle payment
  const handlePayment = () => {
    if (!paymentAmount) {
      message.error("Please enter payment amount");
      return;
    }

    if (parseInt(paymentAmount) < item.price) {
      message.error("Payment amount must be at least the monthly rent");
      return;
    }

    // Mock payment success
    message.success("Payment successful! You will receive a confirmation email shortly.");
    setShowPaymentModal(false);
    setPaymentAmount("");
  };

  return (
    <div className="flex gap-5">
      <div className="w-1/2 flex flex-col gap-5">
        <div className="w-full h-96">
          <img
            src={item.images[selectedImageIndex]}
            className="w-full h-full object-cover rounded-md"
            alt=""
          />
        </div>
        <div className="flex gap-5">
          {item.images.map((image, index) => {
            return (
              <img
                src={image}
                className={`w-24 h-24 object-cover rounded-md cursor-pointer ${
                  selectedImageIndex === index ? "border-2 border-primary" : ""
                }`}
                alt=""
                onClick={() => setSelectedImageIndex(index)}
              />
            );
          })}
        </div>
      </div>

      <div className="w-1/2 flex flex-col gap-5">
        <div>
          <h1 className="text-2xl font-semibold text-orange-900">
            {item.name}
          </h1>
          <span>{item.description}</span>
        </div>

        <Divider />
        <div className="flex flex-col">
          <h1 className="text-2xl font-semibold text-orange-900">
            {isPGView ? "PG Details" : "Product Details"}
          </h1>
          <div className="flex justify-between mt-2">
            <span>Price</span>
            <span>₹ {item.price}{isPGView ? "/month" : ""}</span>
          </div>

          {isPGView ? (
            <>
              <div className="flex justify-between mt-2">
                <span>Location</span>
                <span>{item.location}</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Distance</span>
                <span>{item.distance} away</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Rating</span>
                <span>{item.rating}/5</span>
              </div>
              <div className="flex flex-wrap gap-2 mt-2">
                {item.amenities.map((amenity, index) => (
                  <span key={index} className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
                    {amenity}
                  </span>
                ))}
              </div>
            </>
          ) : (
            <>
              <div className="flex justify-between mt-2">
                <span>Category</span>
                <span className="uppercase">{item.category}</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Bill Available</span>
                <span>{item.billAvailable ? "Yes" : "No"}</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Box Available</span>
                <span>{item.boxAvailable ? "Yes" : "No"}</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Accessories Available</span>
                <span>{item.accessoriesAvailable ? "Yes" : "No"}</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Warranty Available</span>
                <span>{item.warrantyAvailable ? "Yes" : "No"}</span>
              </div>
              <div className="flex justify-between mt-2">
                <span>Purchased Year</span>
                <span>
                  {moment().subtract(item.age, "years").format("YYYY")} ({item.age} years ago)
                </span>
              </div>
            </>
          )}
        </div>

        <Divider />
        <div className="flex flex-col gap-5">
          {isPGView ? (
            <div className="flex justify-between items-center">
              <h1 className="text-2xl font-semibold text-orange-900">Payment</h1>
              <Button type="primary" onClick={() => setShowPaymentModal(true)}>
                Pay Now
              </Button>
            </div>
          ) : (
            <>
              <div className="flex justify-between items-center">
                <h1 className="text-2xl font-semibold text-orange-900">Bids</h1>
                <Button type="primary" onClick={() => setShowBidModal(true)}>
                  Place a Bid
                </Button>
              </div>
              <div className="flex flex-col gap-5">
                {bids.length === 0 ? (
                  <span>No bids yet</span>
                ) : (
                  bids.map((bid) => {
                    return (
                      <div key={bid.id} className="flex justify-between items-center border border-gray-300 p-5 rounded">
                        <div>
                          <span className="font-semibold">{bid.user.name}</span>
                          <br />
                          <span className="text-gray-500">
                            {moment(bid.createdAt).format("MMM DD, YYYY hh:mm a")}
                          </span>
                          {bid.message && (
                            <div className="mt-2 text-gray-600">
                              {bid.message}
                            </div>
                          )}
                        </div>
                        <span className="text-xl font-semibold text-green-700">
                          ₹ {bid.amount}
                        </span>
                      </div>
                    );
                  })
                )}
              </div>
            </>
          )}
        </div>
      </div>

      {/* Bid Modal */}
      <Modal
        title="Place a Bid"
        open={showBidModal}
        onCancel={() => setShowBidModal(false)}
        footer={[
          <Button key="cancel" onClick={() => setShowBidModal(false)}>
            Cancel
          </Button>,
          <Button key="submit" type="primary" onClick={handlePlaceBid}>
            Place Bid
          </Button>
        ]}
      >
        <div className="flex flex-col gap-5">
          <div>
            <label className="block text-gray-700 mb-2">Bid Amount (₹)</label>
            <Input
              type="number"
              value={bidAmount}
              onChange={(e) => setBidAmount(e.target.value)}
              placeholder="Enter your bid amount"
              min={item.price + 1}
            />
            <span className="text-sm text-gray-500 mt-1">
              Minimum bid: ₹{item.price + 1}
            </span>
          </div>
          <div>
            <label className="block text-gray-700 mb-2">Message (Optional)</label>
            <Input.TextArea
              value={bidMessage}
              onChange={(e) => setBidMessage(e.target.value)}
              placeholder="Add a message with your bid"
              rows={4}
            />
          </div>
        </div>
      </Modal>

      {/* Payment Modal */}
      <Modal
        title="Make Payment"
        open={showPaymentModal}
        onCancel={() => setShowPaymentModal(false)}
        footer={[
          <Button key="cancel" onClick={() => setShowPaymentModal(false)}>
            Cancel
          </Button>,
          <Button key="submit" type="primary" onClick={handlePayment}>
            Pay Now
          </Button>
        ]}
      >
        <div className="flex flex-col gap-5">
          <div>
            <label className="block text-gray-700 mb-2">Payment Amount (₹)</label>
            <Input
              type="number"
              value={paymentAmount}
              onChange={(e) => setPaymentAmount(e.target.value)}
              placeholder={`Enter payment amount (minimum ₹${item.price})`}
              min={item.price}
            />
            <span className="text-sm text-gray-500 mt-1">
              Monthly rent: ₹{item.price}
            </span>
          </div>
          <div>
            <label className="block text-gray-700 mb-2">Payment Method</label>
            <select className="w-full p-2 border rounded">
              <option value="credit">Credit Card</option>
              <option value="debit">Debit Card</option>
              <option value="upi">UPI</option>
              <option value="netbanking">Net Banking</option>
            </select>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default ProductInfo;
